import React, { useState } from "react";
import { questions } from "./Questions";

const Form = () => {
  const [openQuestions, setOpenQuestions] = useState([
    true,
    false,
    false,
    false,
    false,
  ]);

  const [selectedOptions, setSelectedOptions] = useState(
    new Array(questions.length).fill(null),
  );

  const toggleQuestion = (index) => {
    setOpenQuestions((prevState) => {
      const newOpenState = [...prevState];

      if (index === 3 && selectedOptions[0] === 0) {
        return prevState;
      }
      newOpenState[index] = !newOpenState[index];
      return newOpenState;
    });
  };

  const handleOptionSelect = (questionIndex, optionIndex) => {
    setSelectedOptions((prevState) => {
      const newSelectedOptions = [...prevState];
      newSelectedOptions[questionIndex] = optionIndex;
      return newSelectedOptions;
    });

    if (questionIndex === 0 && optionIndex === 0) {
      setOpenQuestions((prevState) => {
        const newOpenState = [...prevState];
        newOpenState[3] = false;
        return newOpenState;
      });
      setSelectedOptions((prevState) => {
        const newSelectedOptions = [...prevState];
        newSelectedOptions[3] = null;
        return newSelectedOptions;
      });
    }
  };

  const allQuestionsAnswered = selectedOptions.every(
    (option, index) =>
      option !== null && !(index === 3 && selectedOptions[0] === 0),
  );

  return (
    <div className="form">
      <ul className="form-headers-list">
        {questions.map((question, index) => (
          <li
            key={index}
            className={`${openQuestions[index] ? "active" : ""} ${
              index === 3 && selectedOptions[0] === 0 ? "disabled" : ""
            }`}
          >
            <span>{`0${index + 1}`}</span>
            {question.header}
          </li>
        ))}
      </ul>
      <div className="form-content">
        {questions.map((question, questionIndex) => (
          <div
            key={questionIndex}
            className={`question ${
              selectedOptions[0] === 0 && questionIndex === 3 ? "disabled" : ""
            }`}
          >
            <h2 onClick={() => toggleQuestion(questionIndex)}>
              {question.title}
              <svg
                className={openQuestions[questionIndex] ? "opened-icon" : ""}
                width="19"
                height="13"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M15.949.586l2.828 2.828-9.096 9.096L.586 3.414 3.414.586l6.267 6.267z"
                  fill="#0E8784"
                  fillRule="nonzero"
                />
              </svg>
            </h2>
            {openQuestions[questionIndex] && (
              <div className="options">
                {question.options.map((option, optionIndex) => (
                  <div
                    key={optionIndex}
                    className={`option-card ${
                      selectedOptions[questionIndex] === optionIndex
                        ? "selected"
                        : ""
                    }`}
                    onClick={() =>
                      handleOptionSelect(questionIndex, optionIndex)
                    }
                  >
                    <h4>{option.title}</h4>
                    <p>{option.description}</p>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
        <div className="order-summary">
          <p>Order Summary</p>
          <h4>
            “I drink my coffee {selectedOptions[0] === 0 ? "using" : "as"}{" "}
            <span>
              {selectedOptions[0] !== null
                ? questions[0].options[selectedOptions[0]].title
                : " _____ "}
            </span>
            , with a{" "}
            <span>
              {selectedOptions[1] !== null
                ? questions[1].options[selectedOptions[1]].title
                : " _____ "}
            </span>{" "}
            type of bean.{" "}
            <span>
              {selectedOptions[2] !== null
                ? questions[2].options[selectedOptions[2]].title
                : " _____ "}
            </span>{" "}
            {selectedOptions[0] !== 0 && (
              <>
                {" ground ala "}
                <span>
                  {selectedOptions[3] !== null
                    ? questions[3].options[selectedOptions[3]].title
                    : " _____ "}
                </span>
              </>
            )}
            , sent to me{" "}
            <span>
              {selectedOptions[4] !== null
                ? questions[4].options[selectedOptions[4]].title
                : " _____ "}
            </span>
            .”
          </h4>
        </div>
        <button className="subscribe__button" disabled={!allQuestionsAnswered}>
          Create my plan!
        </button>
      </div>
    </div>
  );
};

export default Form;
