import React, { useState } from "react";
import { questions } from "./Questions";

const Form = () => {
  const [openQuestions, setOpenQuestions] = useState([
    true,
    false,
    false,
    false,
    false,
  ]);

  const [selectedOptions, setSelectedOptions] = useState(
    new Array(questions.length).fill(null),
  );

  const toggleQuestion = (index) => {
    setOpenQuestions((prevState) => {
      const newOpenState = [...prevState];
      newOpenState[index] = !newOpenState[index];
      return newOpenState;
    });
  };

  return (
    <div className="form">
      <ul className="form-headers-list">
        {questions.map((question, index) => (
          <li key={index} className={openQuestions[index] ? "active" : ""}>
            <span>{`0${index + 1}`}</span>
            {question.header}
          </li>
        ))}
      </ul>
      <div className="form-content">
        {questions.map((question, index) => (
          <div key={index} className="question">
            <h2 onClick={() => toggleQuestion(index)}>
              {question.title}
              <svg
                className={openQuestions[index] ? "opened-icon" : ""}
                width="19"
                height="13"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M15.949.586l2.828 2.828-9.096 9.096L.586 3.414 3.414.586l6.267 6.267z"
                  fill="#0E8784"
                  fillRule="nonzero"
                />
              </svg>
            </h2>
            {openQuestions[index] && (
              <div className="options">
                {question.options.map((option, optIndex) => (
                  <div key={optIndex} className="option-card">
                    <h4>{option.title}</h4>
                    <p>{option.description}</p>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
        <div className="order-summary">
          <p>Order Summary</p>
          <h4>
            “I drink my coffee as <span>{selectedOptions[0]}</span>, with a{" "}
            {selectedOptions[1]} type of bean. {selectedOptions[2]} ground ala
            {selectedOptions[3]}, sent to me {selectedOptions[4]}.”
          </h4>
        </div>
        <button className="subscribe__button" disabled>
          Create my plan!
        </button>
      </div>
    </div>
  );
};

export default Form;
