import React, { useState } from "react";
import "./styles.css"; // Ensure CSS is imported correctly

const Form = () => {
  // State to track which questions are open
  const [openQuestions, setOpenQuestions] = useState([
    true,
    false,
    false,
    false,
    false,
  ]);

  // Function to toggle questions
  const toggleQuestion = (index) => {
    setOpenQuestions((prevState) => {
      const newOpenState = [...prevState];
      newOpenState[index] = !newOpenState[index];
      return newOpenState;
    });
  };

  // Questions data
  const questions = [
    {
      title: "How do you drink your coffee?",
      options: [
        {
          title: "Capsule",
          description: "Compatible with Nespresso systems and similar brewers",
        },
        {
          title: "Filter",
          description:
            "For pour over or drip methods like Aeropress, Chemex, and V60",
        },
        {
          title: "Espresso",
          description:
            "Dense and finely ground beans for an intense, flavorful experience",
        },
      ],
    },
    {
      title: "What type of coffee?",
      options: [
        {
          title: "Single Origin",
          description:
            "Distinct, high quality coffee from a specific family-owned farm",
        },
        {
          title: "Decaf",
          description:
            "Just like regular coffee, except the caffeine has been removed",
        },
        {
          title: "Blended",
          description:
            "Combination of two or three dark roasted beans of organic coffees",
        },
      ],
    },
    {
      title: "How much would you like?",
      options: [
        {
          title: "250g",
          description:
            "Perfect for the solo drinker. Yields about 12 delicious cups.",
        },
        {
          title: "500g",
          description:
            "Perfect option for a couple. Yields about 40 delectable cups.",
        },
        {
          title: "1000g",
          description:
            "Perfect for offices and events. Yields about 90 delightful cups.",
        },
      ],
    },
    {
      title: "Want us to grind them?",
      options: [
        {
          title: "Wholebean",
          description: "Best choice if you cherish the full sensory experience",
        },
        {
          title: "Filter",
          description:
            "For drip or pour-over coffee methods such as V60 or Aeropress",
        },
        {
          title: "CafetiÃ©re",
          description:
            "Course ground beans specially suited for french press coffee",
        },
      ],
    },
    {
      title: "How often should we deliver?",
      options: [
        {
          title: "Every week",
          description:
            "$7.20 per shipment. Includes free first-class shipping.",
        },
        {
          title: "Every 2 weeks",
          description: "$9.60 per shipment. Includes free priority shipping.",
        },
        {
          title: "Every month",
          description: "$12.00 per shipment. Includes free priority shipping.",
        },
      ],
    },
  ];

  return (
    <div className="form">
      <ul className="form-headers-list">
        {questions.map((question, index) => (
          <li key={index}>
            <span>{`0${index + 1}`}</span>
            {question.title}
          </li>
        ))}
      </ul>
      <div className="form-content">
        {questions.map((question, index) => (
          <div key={index} className="question">
            <h2 onClick={() => toggleQuestion(index)}>
              {question.title}
              <svg
                className={openQuestions[index] ? "opened-icon" : ""}
                width="19"
                height="13"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M15.949.586l2.828 2.828-9.096 9.096L.586 3.414 3.414.586l6.267 6.267z"
                  fill="#0E8784"
                  fillRule="nonzero"
                />
              </svg>
            </h2>
            {openQuestions[index] && (
              <div className="options">
                {question.options.map((option, optIndex) => (
                  <div key={optIndex} className="option-card">
                    <h4>{option.title}</h4>
                    <p>{option.description}</p>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
        <div className="order-summary">
          <p>Order Summary</p>
          <h4>
            âI drink my coffee as Filter, with a Decaf type of bean. 250g ground
            ala CafetiÃ©re, sent to me Every Week.â
          </h4>
        </div>
        <button className="subscribe__button" disabled>
          Create my plan!
        </button>
      </div>
    </div>
  );
};

export default Form;
